<template>
  <Page shouldUserBeAuthenticated>
    <div id="launch-countdown-page">
      <h1>Les jeux commenceront dans</h1>

      <div id="countdown">
        <div class="coutdown-case">
          <div class="countdown-value">{{ nbDaysRemaining }}</div>
          <div class="countdown-label">Jours</div>
        </div>
        <div class="coutdown-case">
          <div class="countdown-value">{{ nbHoursRemaining }}</div>
          <div class="countdown-label">Heures</div>
        </div>
        <div class="coutdown-case">
          <div class="countdown-value">{{ nbMinutesRemaining }}</div>
          <div class="countdown-label">Minutes</div>
        </div>
        <div class="coutdown-case">
          <div class="countdown-value">{{ nbSecondsRemaining }}</div>
          <div class="countdown-label">Secondes</div>
        </div>
      </div>
    </div>
  </Page>
</template>

<script>
  import Page from '@/layout/Page';

  export default {
    name: 'LaunchCountdownView',
    components: { Page },
    data() {
      return {
        christmasDay: new Date('2021-12-01').getTime(),
        nbDaysRemaining: 0,
        nbHoursRemaining: 0,
        nbMinutesRemaining: 0,
        nbSecondsRemaining: 0,
        interval: undefined,
      };
    },
    methods: {
      calculateTimeRemaining() {
        const now = Date.now();
        const diff = this.christmasDay - now;

        if (diff < 0) clearInterval(this.interval);

        this.nbDaysRemaining = Math.floor(diff / (1000 * 60 * 60 * 24));
        this.nbDaysRemaining = this.nbDaysRemaining >= 0 ? this.nbDaysRemaining : 0;

        this.nbHoursRemaining = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        this.nbHoursRemaining = this.nbHoursRemaining >= 0 ? this.nbHoursRemaining : 0;

        this.nbMinutesRemaining = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
        this.nbMinutesRemaining = this.nbMinutesRemaining >= 0 ? this.nbMinutesRemaining : 0;

        this.nbSecondsRemaining = Math.floor((diff % (1000 * 60)) / 1000);
        this.nbSecondsRemaining = this.nbSecondsRemaining >= 0 ? this.nbSecondsRemaining : 0;
      },
    },
    mounted() {
      this.calculateTimeRemaining();
      this.interval = setInterval(this.calculateTimeRemaining, 1000);
    },
    beforeUnmount() {
      if (this.interval) clearInterval(this.interval);
    },
  };
</script>

